#!/bin/python#encoding=utf-8import sysdef preprocess( filename ):    result = ""    with open(filename, 'r') as f :        for line in f.readlines():            line += "\r\n"            if ord( line[0] ) < 128 :                line = u">"+line            result += line    with open(filename, 'w') as f:        f.write( result )if __name__ == '__main__':    preprocess( sys.args[0] )