#!/bin/python#encoding=utf-8import sysdef preprocess( filename ):    result = u""    with open(filename, 'r') as f :        for line in f.readlines():            line = line.decode('utf8')            line += u"\r\n"            if ord( line[0] ) < 128 :                line = u">"+line            result += line    with open(filename, 'w') as f:        f.write( result.encode('UTF-8') )if __name__ == '__main__':    preprocess( sys.argv[1] )
